import React, { useEffect, useState } from "react";
import { NavLink, useLocation } from "react-router-dom";

const Tabs = () => {
  let location = useLocation();
  const [tabs, setTabs] = useState([]);

  const handleClick = (e, i) => {
    if (!e.target.previousSibling.classList.contains('active-tab')) {
      e.preventDefault();
    }
    const t = tabs.filter((tab, index) => i !== index);
    setTabs([...t]);
  };

  useEffect(() => {
    if (!tabs.includes(location.pathname)) {
      setTabs([...tabs, location.pathname]);
    }
  }, [location, tabs]);

    return (
      <div className="tabs">
        {
          tabs.length && tabs.map((tab, i) => {
            const t = tab.split('/');
            const tabName = t[t.length - 1];
            return (<div className="tab" key={i}>
              <NavLink 
                to={tab} 
                className={({ isActive }) => isActive ? 'active-tab' : undefined}
              >
                {tabName}
              </NavLink>
              {
                tab === '/' ? 
                undefined : 
                <NavLink
                  onClick={e => handleClick(e, i)}
                  to={tabs[i - 1]}
                >x</NavLink>
              }
            </div>)
          })
        }
      </div>
    );
  };

  export default Tabs;

