  -webkit-font-smoothing: antialiased;
  -moz-osx-font-smoothing: grayscale;
  font-size: 14px;
  background-color: rgb(1, 22, 39);
}

code {
  font-family: source-code-pro, Menlo, Monaco, Consolas, 'Courier New', monospace;
}

.layout {
  display: grid;
  height: 100vh;
  grid-template-areas:
    'explorer tabs'
    'nav code';
  grid-template-columns: 300px 1fr;
  grid-template-rows: 40px 1fr;
}

.explorer {
  background-color: hsl(206, 100%, 3%);
  display: flex;
  align-items: center;
  padding-inline-start: 15px; 
}

.explorer h1 {
  color:hsl(0, 0%, 70%);
  font-size: 12px;
  font-weight: 400;
  text-transform: uppercase;
  margin: 0;
  padding: 0;
}

.tabs {
  display: flex;
  background-color: hsl(206, 100%, 3%);
  overflow: scroll;
  column-gap: 1px;
}

.tab {
  display: flex;
  background-color: hsl(206, 100%, 11%);
  color: hsl(0, 0%, 70%);
  padding: 0 10px;
  column-gap: 10px;
}

.tab a {
  color: hsl(0, 0%, 50%);
  text-decoration: none;
  white-space: nowrap;
  display: flex;
  align-items: center;
}

.tab a.active-tab {
  color:hsl(0, 0%, 100%)
}

.tab:has(.active-tab),
.tab:has(.active-tab):hover {
  background-color: hsl(206, 100%, 8%);
}

.tab:hover {
  background-color: hsl(206, 100%, 9%);
}

.nav {
  color: white;
  overflow: scroll;
  background-color: hsl(206, 100%, 3%);
}

.nav ul {
  margin: 0;
  padding-left: 0;
}

.nav ul li summary,
.nav ul li a {
  padding: 4px;
  white-space: nowrap;
  text-overflow: ellipsis;
  overflow: hidden;
}

.nav li {
  display: block;
  position: relative;
}

.nav li:hover {
  cursor: pointer;
}

.nav li:has(li:hover) summary:hover,
.nav li:has(li:hover) a:hover,
.nav > ul > li > details > summary:hover {
  background-color: hsl(206, 100%, 8%);
}

.nav li:has(a.active) a.active {
  background-color: hsl(206, 100%, 6%);
}

